import React, { useState, useEffect } from 'react';
import {
    Container,
    Table,
    TableBody,
    TableCell,
    TableContainer,
    TableHead,
    TableRow,
    Paper,
    IconButton,
    TextField,
    Box,
    Typography,
    Chip
} from '@mui/material';
import { Edit as EditIcon, Delete as DeleteIcon } from '@mui/icons-material';
import axios from 'axios';

const API_URL = 'http://localhost:3001/api';

const ListaCarros = ({ onEditCarro }) => {
    const [carros, setCarros] = useState([]);
    const [filtro, setFiltro] = useState({
        marca: '',
        modelo: '',
        ano: ''
    });

    useEffect(() => {
        carregarCarros();
    }, [filtro]);

    const carregarCarros = async () => {
        try {
            const params = new URLSearchParams();
            if (filtro.marca) params.append('marca', filtro.marca);
            if (filtro.modelo) params.append('modelo', filtro.modelo);
            if (filtro.ano) params.append('ano', filtro.ano);

            const response = await axios.get(`${API_URL}/carros?${params.toString()}`);
            setCarros(response.data);
        } catch (error) {
            console.error('Erro ao carregar carros:', error);
        }
    };

    const handleDelete = async (id) => {
        if (window.confirm('Tem certeza que deseja excluir este carro?')) {
            try {
                await axios.delete(`${API_URL}/carros/${id}`);
                carregarCarros();
            } catch (error) {
                console.error('Erro ao deletar carro:', error);
                alert('Erro ao deletar carro');
            }
        }
    };

    const getClassificacaoColor = (classificacao) => {
        const cores = {
            'E': '#4CAF50', // Verde
            'D': '#8BC34A', // Verde claro
            'C': '#FFC107', // Amarelo
            'B': '#FF9800', // Laranja
            'A': '#FF5722', // Laranja escuro
            'S': '#F44336'  // Vermelho
        };
        return cores[classificacao] || '#757575';
    };

    const formatarValor = (valor) => {
        return new Intl.NumberFormat('pt-BR', {
            style: 'currency',
            currency: 'BRL'
        }).format(valor);
    };

    return (
        <Container maxWidth="lg">
            <Box sx={{ my: 4 }}>
                <Typography variant="h4" component="h2" gutterBottom>
                    Lista de Carros
                </Typography>
                
                <Box sx={{ mb: 3 }}>
                    <TextField
                        label="Filtrar por Marca"
                        value={filtro.marca}
                        onChange={(e) => setFiltro({ ...filtro, marca: e.target.value })}
                        sx={{ mr: 2 }}
                    />
                    <TextField
                        label="Filtrar por Modelo"
                        value={filtro.modelo}
                        onChange={(e) => setFiltro({ ...filtro, modelo: e.target.value })}
                        sx={{ mr: 2 }}
                    />
                    <TextField
                        label="Filtrar por Ano"
                        value={filtro.ano}
                        onChange={(e) => setFiltro({ ...filtro, ano: e.target.value })}
                    />
                </Box>

                <TableContainer component={Paper}>
                    <Table>
                        <TableHead>
                            <TableRow>
                                <TableCell>Marca</TableCell>
                                <TableCell>Modelo</TableCell>
                                <TableCell>Ano</TableCell>
                                <TableCell>Valor</TableCell>
                                <TableCell>Classificação</TableCell>
                                <TableCell>Data Consulta</TableCell>
                                <TableCell>Ações</TableCell>
                            </TableRow>
                        </TableHead>
                        <TableBody>
                            {carros.map((carro) => (
                                <TableRow key={carro.id}>
                                    <TableCell>{carro.marca}</TableCell>
                                    <TableCell>{carro.modelo}</TableCell>
                                    <TableCell>{carro.ano}</TableCell>
                                    <TableCell>{formatarValor(carro.valor)}</TableCell>
                                    <TableCell>
                                        <Chip
                                            label={carro.classificacao}
                                            style={{
                                                backgroundColor: getClassificacaoColor(carro.classificacao),
                                                color: 'white'
                                            }}
                                        />
                                    </TableCell>
                                    <TableCell>
                                        {new Date(carro.data_consulta).toLocaleDateString()}
                                    </TableCell>
                                    <TableCell>
                                        <IconButton
                                            onClick={() => onEditCarro(carro)}
                                            color="primary"
                                        >
                                            <EditIcon />
                                        </IconButton>
                                        <IconButton
                                            onClick={() => handleDelete(carro.id)}
                                            color="error"
                                        >
                                            <DeleteIcon />
                                        </IconButton>
                                    </TableCell>
                                </TableRow>
                            ))}
                        </TableBody>
                    </Table>
                </TableContainer>
            </Box>
        </Container>
    );
};

export default ListaCarros;
