import React, { useState } from 'react';
import {
    Container,
    Table,
    TableBody,
    TableCell,
    TableContainer,
    TableHead,
    TableRow,
    Paper,
    IconButton,
    Box,
    Typography,
    Chip,
    FormControl,
    InputLabel,
    Select,
    MenuItem,
    Grid
} from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';
import EditIcon from '@mui/icons-material/Edit';
import axios from 'axios';

const API_URL = 'http://localhost:3001/api';

const getClassificacaoColor = (classificacao) => {
    const cores = {
        'E': '#2E7D32', // Verde escuro mais suave
        'D': '#43A047', // Verde médio
        'C': '#FBC02D', // Amarelo mais suave
        'B': '#FF8F00', // Laranja mais suave
        'A': '#E64A19', // Laranja escuro mais suave
        'S': '#D32F2F'  // Vermelho mais suave
    };
    return cores[classificacao] || '#757575';
};

const getClassificacaoDescricao = (classificacao) => {
    const descricoes = {
        'E': 'Até R$ 20.000',
        'D': 'R$ 20.001 - R$ 35.000',
        'C': 'R$ 35.001 - R$ 50.000',
        'B': 'R$ 50.001 - R$ 75.000',
        'A': 'R$ 75.001 - R$ 100.000',
        'S': 'Acima de R$ 100.000'
    };
    return descricoes[classificacao] || '';
};

const ListaCarros = ({ carros, onEditCarro, onDeleteCarro }) => {
    const [filtroClassificacao, setFiltroClassificacao] = useState('');

    const handleDelete = async (id) => {
        if (window.confirm('Tem certeza que deseja excluir este carro?')) {
            try {
                await axios.delete(`${API_URL}/carros/${id}`);
                onDeleteCarro();
            } catch (error) {
                console.error('Erro ao deletar carro:', error);
                alert('Erro ao deletar carro');
            }
        }
    };

    const formatarValor = (valor) => {
        return new Intl.NumberFormat('pt-BR', {
            style: 'currency',
            currency: 'BRL'
        }).format(valor);
    };

    const formatarData = (data) => {
        return new Date(data).toLocaleDateString('pt-BR');
    };

    const carrosFiltrados = filtroClassificacao
        ? carros.filter(carro => carro.classificacao === filtroClassificacao)
        : carros;

    const carrosAgrupados = carrosFiltrados.reduce((acc, carro) => {
        if (!acc[carro.classificacao]) {
            acc[carro.classificacao] = [];
        }
        acc[carro.classificacao].push(carro);
        return acc;
    }, {});

    const classificacoes = ['E', 'D', 'C', 'B', 'A', 'S'];

    return (
        <Container maxWidth="md">
            <Box sx={{ my: 4 }}>
                <Grid container spacing={2} alignItems="center" sx={{ mb: 3 }}>
                    <Grid item xs={12} md={6}>
                        <Typography variant="h4" component="h2">
                            Carros Consultados
                        </Typography>
                    </Grid>
                    <Grid item xs={12} md={6}>
                        <FormControl fullWidth>
                            <InputLabel>Filtrar por Classificação</InputLabel>
                            <Select
                                value={filtroClassificacao}
                                onChange={(e) => setFiltroClassificacao(e.target.value)}
                                label="Filtrar por Classificação"
                            >
                                <MenuItem value="">Todas as Classificações</MenuItem>
                                {classificacoes.map((classificacao) => (
                                    <MenuItem key={classificacao} value={classificacao}>
                                        {classificacao} - {getClassificacaoDescricao(classificacao)}
                                    </MenuItem>
                                ))}
                            </Select>
                        </FormControl>
                    </Grid>
                </Grid>

                {Object.entries(carrosAgrupados).map(([classificacao, carrosGrupo]) => (
                    <Box key={classificacao} sx={{ mb: 4 }}>
                        <Typography variant="h6" sx={{ mb: 2, display: 'flex', alignItems: 'center' }}>
                            <Chip
                                label={`Classificação ${classificacao} - ${getClassificacaoDescricao(classificacao)}`}
                                style={{
                                    backgroundColor: getClassificacaoColor(classificacao),
                                    color: 'white',
                                    marginRight: '10px'
                                }}
                            />
                            ({carrosGrupo.length} {carrosGrupo.length === 1 ? 'carro' : 'carros'})
                        </Typography>
                        <TableContainer component={Paper}>
                            <Table>
                                <TableHead>
                                    <TableRow>
                                        <TableCell>Marca</TableCell>
                                        <TableCell>Modelo</TableCell>
                                        <TableCell>Ano</TableCell>
                                        <TableCell>Valor</TableCell>
                                        <TableCell>Data Consulta</TableCell>
                                        <TableCell>Ações</TableCell>
                                    </TableRow>
                                </TableHead>
                                <TableBody>
                                    {carrosGrupo.map((carro) => (
                                        <TableRow key={carro.id}>
                                            <TableCell>{carro.marca}</TableCell>
                                            <TableCell>{carro.modelo}</TableCell>
                                            <TableCell>{carro.ano}</TableCell>
                                            <TableCell>{formatarValor(carro.valor)}</TableCell>
                                            <TableCell>{formatarData(carro.data_consulta)}</TableCell>
                                            <TableCell>
                                                <IconButton onClick={() => onEditCarro(carro)}>
                                                    <EditIcon />
                                                </IconButton>
                                                <IconButton onClick={() => handleDelete(carro.id)}>
                                                    <DeleteIcon />
                                                </IconButton>
                                            </TableCell>
                                        </TableRow>
                                    ))}
                                </TableBody>
                            </Table>
                        </TableContainer>
                    </Box>
                ))}
            </Box>
        </Container>
    );
};

export default ListaCarros;
