import React, { useState, useEffect } from 'react';
import {
    Container,
    Table,
    TableBody,
    TableCell,
    TableContainer,
    TableHead,
    TableRow,
    Paper,
    IconButton,
    Box,
    Typography
} from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';
import EditIcon from '@mui/icons-material/Edit';
import axios from 'axios';

const API_URL = 'http://localhost:3001/api';

const ListaCarros = ({ onEditCarro }) => {
    const [carros, setCarros] = useState([]);

    const carregarCarros = async () => {
        try {
            const response = await axios.get(`${API_URL}/carros`);
            console.log('Carros carregados:', response.data);
            setCarros(response.data);
        } catch (error) {
            console.error('Erro ao carregar carros:', error);
        }
    };

    useEffect(() => {
        carregarCarros();
    }, []);

    const handleDelete = async (id) => {
        try {
            await axios.delete(`${API_URL}/carros/${id}`);
            carregarCarros();
        } catch (error) {
            console.error('Erro ao deletar carro:', error);
            alert('Erro ao deletar carro');
        }
    };

    const formatarValor = (valor) => {
        return new Intl.NumberFormat('pt-BR', {
            style: 'currency',
            currency: 'BRL'
        }).format(valor);
    };

    const formatarData = (data) => {
        return new Date(data).toLocaleDateString('pt-BR');
    };

    return (
        <Container maxWidth="md">
            <Box sx={{ my: 4 }}>
                <Typography variant="h4" component="h2" gutterBottom>
                    Carros Consultados
                </Typography>
                <TableContainer component={Paper}>
                    <Table>
                        <TableHead>
                            <TableRow>
                                <TableCell>Marca</TableCell>
                                <TableCell>Modelo</TableCell>
                                <TableCell>Ano</TableCell>
                                <TableCell>Valor</TableCell>
                                <TableCell>Data Consulta</TableCell>
                                <TableCell>Ações</TableCell>
                            </TableRow>
                        </TableHead>
                        <TableBody>
                            {carros.map((carro) => (
                                <TableRow key={carro.id}>
                                    <TableCell>{carro.marca}</TableCell>
                                    <TableCell>{carro.modelo}</TableCell>
                                    <TableCell>{carro.ano}</TableCell>
                                    <TableCell>{formatarValor(carro.valor)}</TableCell>
                                    <TableCell>{formatarData(carro.data_consulta)}</TableCell>
                                    <TableCell>
                                        <IconButton onClick={() => onEditCarro(carro)}>
                                            <EditIcon />
                                        </IconButton>
                                        <IconButton onClick={() => handleDelete(carro.id)}>
                                            <DeleteIcon />
                                        </IconButton>
                                    </TableCell>
                                </TableRow>
                            ))}
                        </TableBody>
                    </Table>
                </TableContainer>
            </Box>
        </Container>
    );
};

export default ListaCarros;
