import React, { useState, useEffect } from 'react';
import {
    Container,
    TextField,
    Button,
    Grid,
    MenuItem,
    FormControl,
    InputLabel,
    Select,
    Box,
    Typography
} from '@mui/material';
import axios from 'axios';

const API_URL = 'http://localhost:3001/api';

const Busca = ({ onCarroEncontrado }) => {
    const [marcas, setMarcas] = useState([]);
    const [modelos, setModelos] = useState([]);
    const [anos, setAnos] = useState([]);
    const [selectedMarca, setSelectedMarca] = useState('');
    const [selectedModelo, setSelectedModelo] = useState('');
    const [selectedAno, setSelectedAno] = useState('');
    const [loading, setLoading] = useState(false);

    useEffect(() => {
        carregarMarcas();
    }, []);

    const carregarMarcas = async () => {
        try {
            const response = await axios.get(`${API_URL}/marcas`);
            setMarcas(response.data);
        } catch (error) {
            console.error('Erro ao carregar marcas:', error);
        }
    };

    const carregarModelos = async (marcaId) => {
        try {
            const response = await axios.post(`${API_URL}/modelos`, { marcaId });
            setModelos(response.data.Modelos);
        } catch (error) {
            console.error('Erro ao carregar modelos:', error);
        }
    };

    const carregarAnos = async (modeloId) => {
        try {
            const response = await axios.post(`${API_URL}/anos`, { modeloId });
            setAnos(response.data);
        } catch (error) {
            console.error('Erro ao carregar anos:', error);
        }
    };

    const handleMarcaChange = (event) => {
        const marcaId = event.target.value;
        setSelectedMarca(marcaId);
        setSelectedModelo('');
        setSelectedAno('');
        setModelos([]);
        setAnos([]);
        if (marcaId) {
            carregarModelos(marcaId);
        }
    };

    const handleModeloChange = (event) => {
        const modeloId = event.target.value;
        setSelectedModelo(modeloId);
        setSelectedAno('');
        setAnos([]);
        if (modeloId) {
            carregarAnos(modeloId);
        }
    };

    const handleAnoChange = (event) => {
        setSelectedAno(event.target.value);
    };

    const handleBuscar = async () => {
        if (!selectedMarca || !selectedModelo || !selectedAno) {
            alert('Por favor, selecione marca, modelo e ano');
            return;
        }

        setLoading(true);
        try {
            const response = await axios.post(`${API_URL}/valor`, {
                marcaId: selectedMarca,
                modeloId: selectedModelo,
                anoId: selectedAno
            });

            const carro = {
                marca: marcas.find(m => m.Value === selectedMarca)?.Label,
                modelo: modelos.find(m => m.Value === selectedModelo)?.Label,
                ano: anos.find(a => a.Value === selectedAno)?.Label,
                valor: parseFloat(response.data.Valor.replace('R$ ', '').replace('.', '').replace(',', '.')),
                data_consulta: new Date().toISOString()
            };

            onCarroEncontrado(carro);
        } catch (error) {
            console.error('Erro ao buscar valor:', error);
            alert('Erro ao buscar valor do veículo');
        } finally {
            setLoading(false);
        }
    };

    return (
        <Container maxWidth="md">
            <Box sx={{ my: 4 }}>
                <Typography variant="h4" component="h1" gutterBottom>
                    Consulta FIPE
                </Typography>
                <Grid container spacing={3}>
                    <Grid item xs={12} md={4}>
                        <FormControl fullWidth>
                            <InputLabel>Marca</InputLabel>
                            <Select
                                value={selectedMarca}
                                onChange={handleMarcaChange}
                                label="Marca"
                            >
                                {marcas.map((marca) => (
                                    <MenuItem key={marca.Value} value={marca.Value}>
                                        {marca.Label}
                                    </MenuItem>
                                ))}
                            </Select>
                        </FormControl>
                    </Grid>
                    <Grid item xs={12} md={4}>
                        <FormControl fullWidth>
                            <InputLabel>Modelo</InputLabel>
                            <Select
                                value={selectedModelo}
                                onChange={handleModeloChange}
                                label="Modelo"
                                disabled={!selectedMarca}
                            >
                                {modelos.map((modelo) => (
                                    <MenuItem key={modelo.Value} value={modelo.Value}>
                                        {modelo.Label}
                                    </MenuItem>
                                ))}
                            </Select>
                        </FormControl>
                    </Grid>
                    <Grid item xs={12} md={4}>
                        <FormControl fullWidth>
                            <InputLabel>Ano</InputLabel>
                            <Select
                                value={selectedAno}
                                onChange={handleAnoChange}
                                label="Ano"
                                disabled={!selectedModelo}
                            >
                                {anos.map((ano) => (
                                    <MenuItem key={ano.Value} value={ano.Value}>
                                        {ano.Label}
                                    </MenuItem>
                                ))}
                            </Select>
                        </FormControl>
                    </Grid>
                    <Grid item xs={12}>
                        <Button
                            variant="contained"
                            color="primary"
                            onClick={handleBuscar}
                            disabled={loading || !selectedMarca || !selectedModelo || !selectedAno}
                            fullWidth
                        >
                            {loading ? 'Buscando...' : 'Buscar Valor'}
                        </Button>
                    </Grid>
                </Grid>
            </Box>
        </Container>
    );
};

export default Busca;
