import React, { useState } from 'react';
import { Container, CssBaseline, ThemeProvider, createTheme } from '@mui/material';
import Busca from './components/Busca';
import ListaCarros from './components/ListaCarros';
import axios from 'axios';

const API_URL = 'http://localhost:3001/api';

const theme = createTheme({
  palette: {
    primary: {
      main: '#1976d2',
    },
    secondary: {
      main: '#dc004e',
    },
  },
});

function App() {
  const [carroEditando, setCarroEditando] = useState(null);

  const handleCarroEncontrado = async (carro) => {
    try {
      if (carroEditando) {
        await axios.put(`${API_URL}/carros/${carroEditando.id}`, carro);
      } else {
        await axios.post(`${API_URL}/carros`, carro);
      }
      setCarroEditando(null);
    } catch (error) {
      console.error('Erro ao salvar carro:', error);
      alert('Erro ao salvar carro');
    }
  };

  const handleEditCarro = (carro) => {
    setCarroEditando(carro);
  };

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <Container>
        <Busca onCarroEncontrado={handleCarroEncontrado} />
        <ListaCarros onEditCarro={handleEditCarro} />
      </Container>
    </ThemeProvider>
  );
}

export default App;
