import React, { useState, useEffect, useCallback } from 'react';
import { Container, CssBaseline, ThemeProvider, createTheme } from '@mui/material';
import Busca from './components/Busca';
import ListaCarros from './components/ListaCarros';
import axios from 'axios';

const API_URL = 'http://localhost:3001/api';

const theme = createTheme({
  palette: {
    primary: {
      main: '#1976d2',
    },
    secondary: {
      main: '#dc004e',
    },
  },
});

const classificarValor = (valor) => {
  if (valor <= 20000) return 'E';
  if (valor <= 35000) return 'D';
  if (valor <= 50000) return 'C';
  if (valor <= 75000) return 'B';
  if (valor <= 100000) return 'A';
  return 'S';
};

function App() {
  const [carros, setCarros] = useState([]);
  const [carrosImagens, setCarrosImagens] = useState({});
  const [carroEditando, setCarroEditando] = useState(null);

  const carregarCarros = useCallback(async () => {
    try {
      const response = await axios.get(`${API_URL}/carros`);
      setCarros(response.data);
      buscarImagensCarros(response.data);
    } catch (error) {
      console.error('Erro ao carregar carros:', error);
    }
  }, []);

  useEffect(() => {
    carregarCarros();
  }, [carregarCarros]);

  const handleCarroEncontrado = async (carro) => {
    try {
      const carroComClassificacao = {
        ...carro,
        classificacao: carro.classificacao || classificarValor(carro.valor)
      };
      
      const response = await axios.post(`${API_URL}/carros`, carroComClassificacao);
      console.log('Carro salvo:', response.data);
      
      // Atualiza a lista após salvar
      carregarCarros();
    } catch (error) {
      console.error('Erro ao salvar carro:', error);
      alert('Erro ao salvar carro');
    }
  };

  const handleEditCarro = (carro) => {
    setCarroEditando(carro);
  };

  // Função para buscar imagens dos carros
  const buscarImagensCarros = async (carros) => {
    const imagens = {};
    for (const carro of carros) {
      try {
        const response = await axios.get(`${API_URL}/car-images`, {
          params: {
            make: carro.marca,
            year: carro.ano
          }
        });
        const data = response.data;
        if (data.Models && data.Models.length > 0) {
          imagens[carro.id] = data.Models[0].model_image;
        }
      } catch (error) {
        console.error('Erro ao buscar imagem do carro:', error);
      }
    }
    setCarrosImagens(imagens);
  };

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <Container>
        <Busca onCarroEncontrado={handleCarroEncontrado} />
        <ListaCarros 
          carros={carros} 
          onEditCarro={handleEditCarro}
          onDeleteCarro={carregarCarros}
          carroEditando={carroEditando}
        />
        {carros.map((carro) => (
          <div key={carro.id} className="carro-card">
            {carrosImagens[carro.id] && (
              <img 
                src={carrosImagens[carro.id]} 
                alt={`${carro.marca} ${carro.modelo}`}
                className="carro-imagem"
              />
            )}
            <h3>{carro.marca} {carro.modelo}</h3>
            <p>Ano: {carro.ano}</p>
            <p>Preço: R$ {carro.valor.toLocaleString('pt-BR')}</p>
          </div>
        ))}
      </Container>
    </ThemeProvider>
  );
}

export default App;
